import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cartIsVisible: false,
};

export const uiSlice = createSlice({
  name: "ui",
  initialState,
  reducers: {
    toggle(state,action) {
      return {
        ...state,
        cartIsVisible: action.payload,
      };
    },
    
  },
});

export const {toggle} = uiSlice.actions;

export default uiSlice.reducer;
